resource_name = #{resourceName}, resource_desc = #{resourceDesc}, resource_type = #{resourceType}, resource_auth_type = #{resourceAuthType}, resource_rows_count = #{resourceRowsCount}, resource_column_count = #{resourceColumnCount}, resource_column_name_list = #{resourceColumnNameList}, resource_contains_y = #{resourceContainsY}, resource_y_rows_count = #{resourceYRowsCount}, resource_y_ratio = #{resourceYRatio}, resource_tag = #{resourceTag}, organ_id = #{organId}, is_del = #{isDel}, resource_state = #{resourceState}, resource_hash_code = #{resourceHashCode}, user_name = #{userName}, u_time = now()
where id = #{id}

update fusion_resource_field
field_as = #{fieldAs}, field_type = #{fieldType}, field_desc = #{fieldDesc}, u_time = now()
where field_id = #{fieldId}

